class Car {
  constructor(brand, year) {
    this.brand = brand;
    this.year = year;
    console.log(`Brand: ${brand}, Year: ${year}`);
  }
}
new Car('Toyota', 2020);

class Car {
  drive() {
    console.log('Driving...');
  }
}
const myCar = new Car();
myCar.drive();

class Car {
  constructor(brand, year) { this.brand = brand; this.year = year; }
}
class ElectricCar extends Car {
  constructor(brand, year, battery) {
    super(brand, year);
    this.battery = battery;
  }
}
const e = new ElectricCar('Tesla', 2022, 100);

class Car {
  static isEcoFriendly(type) {
    return type === 'Electric';
  }
}
console.log(Car.isEcoFriendly('Electric'));

class Car { drive() { console.log('Driving...'); } }
class ElectricCar extends Car {
  drive() {
    super.drive();
    console.log('Driving silently...');
  }
}
new ElectricCar().drive();

class Product {
  set price(val) {
    this._price = val < 0 ? 0 : val;
  }
  get price() {
    return this._price;
  }
}
const p = new Product(); p.price = -500; console.log(p.price);

