let year = 2000;
if ((year % 4 === 0 && year % 100 !== 0) || (year % 400 === 0)) {
  console.log(true);
} else {
  console.log(false);
}

let op = '*', a = 5, b = 4;
switch(op) {
  case '+': console.log(a + b); break;
  case '-': console.log(a - b); break;
  case '*': console.log(a * b); break;
  case '/': console.log(a / b); break;
}

const obj = { active: true };
if (!obj) return;
if (!obj.active) return;
console.log('Active');
