let i = 1, sum = 0;
while (i <= 100) {
  sum += i;
  i++;
}
console.log(sum);

const fruits = ['Apple', 'Orange', 'Pear'];
for (const fruit of fruits) {
  console.log(fruit);
}

const person = { name: 'Peter', age: 40, city: 'London' };
for (let key in person) {
  console.log(`${key}: ${person[key]}`);
}

for (let i = 1; i <= 5; i++) {
  let line = '';
  for (let j = 1; j <= i; j++) {
    line += '*';
  }
  console.log(line);
}

const nums = [10, 5, 20, 8, 12];
let max = nums[0];
for (let n of nums) {
  if (n > max) max = n;
}
console.log(max);

const arr = ['Apple', 'Orange', 'Banana', 'Grape'];
let foundIndex = -1;
for (let i = 0; i < arr.length; i++) {
  if (arr[i] === 'Banana') {
    foundIndex = i;
    break;
  }
}
console.log(foundIndex);
