const multiply = function(a, b) {
  return a * b;
};
console.log(multiply(5, 6));

function square(n = 1) {
  return n * n;
}
console.log(square());

let x = 'global';
function checkScope() {
  let x = 'local';
  console.log(x); 
}
checkScope();
console.log(x);

function factorial(n) {
  if (n <= 1) return 1;
  return n * factorial(n - 1);
}
console.log(factorial(5));

function add(a, b) { return a + b; }
function apply(n1, n2, callback) {
  return callback(n1, n2);
}
console.log(apply(10, 20, add));

function sumAll() {
  let total = 0;
  for (let i = 0; i < arguments.length; i++) {
    total += arguments[i];
  }
  return total;
}
console.log(sumAll(1, 2, 3, 4));
