let age = 15;
if (age < 18) {
  throw new Error('Age too low');
}

try {
  console.log('Searching');
} catch (e) {
  console.log('Error');
} finally {
  console.log('Done');
}

function checkDivide(a, b) {
  if (b === 0) throw new TypeError('Cannot divide by zero');
  return a / b;
}
checkDivide(10, 0);

console.time('loop_timer');
for(let i=0; i<1000000; i++) {}
console.timeEnd('loop_timer');

function safeParse(str) {
  try {
    return JSON.parse(str);
  } catch (err) {
    return {};
  }
}
console.log(safeParse('hello'));

function checkScore(n) {
  console.assert(n > 0, 'Score must be > 0');
}
checkScore(-5);

let attempts = 0;
while (attempts < 3) {
  try {
    throw new Error('failing'); 
  } catch (e) {
    attempts++;
    if (attempts === 3) console.log('Failed');
  }
}
